package com.jlt.vote.bis.campaign.entity;import com.xcrm.cloud.database.db.annotation.PrimaryKeyField;import com.xcrm.cloud.database.db.annotation.Table;import java.io.Serializable;import java.util.Date;/** * 活动表 * @Author gaoyan * @Date: 2017/7/8 */@Table(tableName = "t_t_campaign")public class Campaign implements Serializable {	/**	 * 活动唯一标识	 */	@PrimaryKeyField	private Long chainId;	/**	 * 报名数量     */	private Integer signCount;	/**	 * 活动开始时间     */	private Date startTime;	/**	 * 结束时间	 */	private Date endTime;	/**	 * 主办方图片	 */	private String sponsorPic;	/**	 * 主办方介绍	 */	private String sponsorIntro;	/**	 * 删除标示 1:是  0:否     */	private Boolean dataStatus;	public Long getChainId() {		return chainId;	}	public void setChainId(Long chainId) {		this.chainId = chainId;	}	public Integer getSignCount() {		return signCount;	}	public void setSignCount(Integer signCount) {		this.signCount = signCount;	}	public Date getStartTime() {		return startTime;	}	public void setStartTime(Date startTime) {		this.startTime = startTime;	}	public Boolean getDataStatus() {		return dataStatus;	}	public void setDataStatus(Boolean dataStatus) {		this.dataStatus = dataStatus;	}	public String getSponsorPic() {		return sponsorPic;	}	public void setSponsorPic(String sponsorPic) {		this.sponsorPic = sponsorPic;	}	public String getSponsorIntro() {		return sponsorIntro;	}	public void setSponsorIntro(String sponsorIntro) {		this.sponsorIntro = sponsorIntro;	}	public Date getEndTime() {		return endTime;	}	public void setEndTime(Date endTime) {		this.endTime = endTime;	}	@Override	public String toString() {		final StringBuilder sb = new StringBuilder("Campaign{");		sb.append("chainId=").append(chainId);		sb.append(", signCount=").append(signCount);		sb.append(", startTime=").append(startTime);		sb.append(", endTime=").append(endTime);		sb.append(", sponsorPic='").append(sponsorPic).append('\'');		sb.append(", sponsorIntro='").append(sponsorIntro).append('\'');		sb.append(", dataStatus=").append(dataStatus);		sb.append('}');		return sb.toString();	}}