package com.jlt.vote.bis.campaign.vo;import java.io.Serializable;import java.math.BigDecimal;/** * 活动礼物详情 * * @Author gaoyan * @Date: 2017/7/8 */public class CampaignGiftDetailVo implements Serializable {    /**     * 礼物id     */    private Long giftId;    /**     * 礼物名称     */    private String giftName;    /**     * 礼物图片     */    private String giftpic;    /**     * 礼物点数     */    private Integer giftPoint;    /**     * 礼物金额     */    private BigDecimal giftFee;    /**     * 礼物兑换票数     */    private Integer voteCount;    public Long getGiftId() {        return giftId;    }    public void setGiftId(Long giftId) {        this.giftId = giftId;    }    public String getGiftName() {        return giftName;    }    public void setGiftName(String giftName) {        this.giftName = giftName;    }    public String getGiftpic() {        return giftpic;    }    public void setGiftpic(String giftpic) {        this.giftpic = giftpic;    }    public Integer getGiftPoint() {        return giftPoint;    }    public void setGiftPoint(Integer giftPoint) {        this.giftPoint = giftPoint;    }    public BigDecimal getGiftFee() {        return giftFee;    }    public void setGiftFee(BigDecimal giftFee) {        this.giftFee = giftFee;    }    public Integer getVoteCount() {        return voteCount;    }    public void setVoteCount(Integer voteCount) {        this.voteCount = voteCount;    }}