package com.jlt.vote.bis.entity;import com.xcrm.cloud.database.db.annotation.PrimaryKeyField;import com.xcrm.cloud.database.db.annotation.Table;import java.io.Serializable;import java.util.Date;/** * 活动表 * @Author gaoyan * @Date: 2017/7/8 */@Table(tableName = "t_t_campaign")public class Campaign implements Serializable {	/**	 * 活动唯一标识	 */	@PrimaryKeyField	private Long chainId;	/**	 * 报名数量     */	private Integer signCount;	/**	 * 投票数量     */	private Integer voteCount;	/**	 * 活动开始时间     */	private Date startTime;	/**	 * 访问数量	 */	private Integer viewCount;	/**	 * 主办方图片	 */	private String sponsorPic;	/**	 * 主办方介绍	 */	private String sponsorIntro;	/**	 * 主办方地址	 */	private String sponsorAddress;	/**	 * 主办方电话	 */	private String sponsorMobile;	/**	 * 删除标示 1:是  0:否     */	private Boolean dataStatus;	public Long getChainId() {		return chainId;	}	public void setChainId(Long chainId) {		this.chainId = chainId;	}	public Integer getSignCount() {		return signCount;	}	public void setSignCount(Integer signCount) {		this.signCount = signCount;	}	public Integer getVoteCount() {		return voteCount;	}	public void setVoteCount(Integer voteCount) {		this.voteCount = voteCount;	}	public Date getStartTime() {		return startTime;	}	public void setStartTime(Date startTime) {		this.startTime = startTime;	}	public Integer getViewCount() {		return viewCount;	}	public void setViewCount(Integer viewCount) {		this.viewCount = viewCount;	}	public Boolean getDataStatus() {		return dataStatus;	}	public void setDataStatus(Boolean dataStatus) {		this.dataStatus = dataStatus;	}	public String getSponsorPic() {		return sponsorPic;	}	public void setSponsorPic(String sponsorPic) {		this.sponsorPic = sponsorPic;	}	public String getSponsorIntro() {		return sponsorIntro;	}	public void setSponsorIntro(String sponsorIntro) {		this.sponsorIntro = sponsorIntro;	}	public String getSponsorAddress() {		return sponsorAddress;	}	public void setSponsorAddress(String sponsorAddress) {		this.sponsorAddress = sponsorAddress;	}	public String getSponsorMobile() {		return sponsorMobile;	}	public void setSponsorMobile(String sponsorMobile) {		this.sponsorMobile = sponsorMobile;	}	@Override	public String toString() {		final StringBuilder sb = new StringBuilder("Campaign{");		sb.append("chainId=").append(chainId);		sb.append(", signCount=").append(signCount);		sb.append(", voteCount=").append(voteCount);		sb.append(", startTime=").append(startTime);		sb.append(", viewCount=").append(viewCount);		sb.append(", sponsorPic='").append(sponsorPic).append('\'');		sb.append(", sponsorIntro='").append(sponsorIntro).append('\'');		sb.append(", sponsorAddress='").append(sponsorAddress).append('\'');		sb.append(", sponsorMobile='").append(sponsorMobile).append('\'');		sb.append(", dataStatus=").append(dataStatus);		sb.append('}');		return sb.toString();	}}