package com.jlt.vote.bis.wx.vo;import javax.validation.constraints.NotNull;import java.io.Serializable;/** * 预支付请求参数 * @Author gaoyan * @Date: 2017/7/8 */public class VotePrepayRequest implements Serializable {	/**	 * 店铺id	 */	@NotNull(message="{vote.chainId.invalid}")	private Long chainId;	/**	 * 用户id	 */	@NotNull(message="{vote.userId.invalid}")	private Long userId;	/**	 * 礼物id	 */	@NotNull(message="{vote.giftId.invalid}")	private Long giftId;	/**	 * 礼物数量     */	private Integer giftCount;	/**	 * openid	 */	private String openid="oTMo21YNuO1BZqdPOIWGO1l6c5v0";	public Long getChainId() {		return chainId;	}	public void setChainId(Long chainId) {		this.chainId = chainId;	}	public Long getUserId() {		return userId;	}	public void setUserId(Long userId) {		this.userId = userId;	}	public Long getGiftId() {		return giftId;	}	public void setGiftId(Long giftId) {		this.giftId = giftId;	}	public Integer getGiftCount() {		return giftCount;	}	public void setGiftCount(Integer giftCount) {		this.giftCount = giftCount;	}	public String getOpenid() {		return openid;	}	public void setOpenid(String openid) {		this.openid = openid;	}	@Override	public String toString() {		final StringBuilder sb = new StringBuilder("VotePrepayRequest{");		sb.append("chainId=").append(chainId);		sb.append(", userId=").append(userId);		sb.append(", giftId=").append(giftId);		sb.append(", giftCount=").append(giftCount);		sb.append(", openid='").append(openid).append('\'');		sb.append('}');		return sb.toString();	}}